mapper.update:
    UPDATE W_MONTHLY_PLAN
  	SET isCompleted = '1'
  	WHERE 
  		member_id = #{member_id}
		AND ID = #{monthlyPlanId}

    UPDATE W_MONTHLY_PLAN
  	SET isCompleted = '0'
  	WHERE 
  		member_id = #{member_id}
		AND ID = #{monthlyPlanId}

    UPDATE W_MONTHLY_PLAN
  	SET month = TO_DATE(#{month},'yyyy-mm')
  	WHERE 
  		member_id = #{member_id}
		AND ID = #{id}
