mapper.update:
    UPDATE W_weekly_plan
  	SET 
  		importance = #{importance},
  		title = #{title},
  		content = #{content}
  	WHERE 
  		member_id = #{member_id}
		AND ID = #{id}

    UPDATE W_weekly_plan
  	SET 
  		isCompleted = '1'
  	WHERE 
  		member_id = #{member_id}
		AND ID = #{weeklyPlanId}

    UPDATE W_weekly_plan
  	SET 
  		isCompleted = '0'
  	WHERE 
  		member_id = #{member_id}
		AND ID = #{weeklyPlanId}
